import styled from 'styled-components';
import theme from '../../../theme/theme';

const PlanningsStyled = styled.div`
  h2.title, p.sub-txt {
    margin: 1em 5em;
  }

  h3 {
    text-align: center;
    background-color: ${theme.colors.orange};
    padding: 1em 0;
  }
`;

export default PlanningsStyled;
