import styled from 'styled-components';
import theme from '../../../theme/theme';

const CartProductStyled = styled.div`
  color: ${theme.colors.black};
  text-align: center;
  margin: 1.2em;

  img {
    max-width: 100%;
    border: 1px solid ${theme.colors.black}
  }
  div.txt-content {
    text-align: left;
  }
`;

export default CartProductStyled;
