import styled from 'styled-components';
import theme from '../../../theme/theme';

const ProductStyled = styled.div`
  div.fildAriane  {
    margin: 1em 7em;
    display: flex;
    justify-content: center;
    align-items: baseline;

    img {
      width: 20px;
      height: auto;
      transform: rotate(180deg);
      margin-right: .7em;
    }

    a {
      color: ${theme.colors.black};
    }
  }
  div.product-zn {
    margin: 0 1em;

    img {
      width: 100%;
      height: auto;
      border: 1px solid ${theme.colors.black};
    }

    div.header {
      display: flex;
      justify-content: space-between;
      align-items: baseline;

      h3, p {
        font-weight: bold;
        margin-bottom: 0;
      }

      p.price {
        font-family: ${theme.fonts.special};
        font-size: 1.2em;
      }
    }

    p.description{
      font-style: italic;
    }

    div.btn-zn {
      display: flex;
      justify-content: space-between;

      div.quantity-zn {
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;

        button {
          width: 100%;
          background-color: ${theme.colors.purple};
          padding: 4px;
          font-size: 1.2em;
        }
      }

      div {
        width: 49%;
      }
    }
  }
`;


export default ProductStyled;
