import styled from 'styled-components';
import theme from '../../../theme/theme';

const FooterStyled = styled.footer`
  background-color: ${theme.colors.purple};
  color: white;
  text-align: center;
  font-size: .7em;
  margin: 2em 0;
  padding: 1.5em;

  div{
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
  }

  div>img {
    max-width: 100px;
    margin: 0 3em;
  }

  ul {
    list-style: none;
    text-align: left;
    margin: 0;
    padding: 0;
    // max-width: 75px;

    li>a {
      color : white;
      text-decoration: none;
    }
  }

  p {
    font-size: .6em;
  }
`;

export default FooterStyled;
