      <div className="txt-content">
        <div className="main">
          <h3>{title}</h3>
          <p className="price">{price} â‚¬/kg</p>
        </div>
        <p className="shortdescription">{shortDescription}</p>
        <p className="bio">{bio}</p>
        <p className="origine">Origine : {origin}</p>
      </div>
      <Button content="Consulter le produit" />
    </CartProductStyled>
  );
};

