import styled from 'styled-components';
import theme from '../../../theme/theme';

const GTCStyled = styled.div`
  text-align: center;
  margin: 0 1em;

  h2 {
    margin: 1em 4em;
  }

  p.sub-txt {
    margin: 0 5em 2em;
  }

  p.txt-content {
    text-align: left;
  }

  @media (min-width: 900px) {
    margin-right: 6em;
    margin-left: 6em;
  }

`;

export default GTCStyled;
